pkg load statistics% Setear la semillarand ("seed", 123);% N simulacionesN=1000;% simular V.AU = rand(N,1);% V.A solicitadaX= 3*sqrt(U);% simulaciones de la variable independienten = 100;% V.Ax = linspace(-0.1,3.1,n);% constante h de filtradoh = 0.2;%  pdf a estimarpdf_x = zeros(size(x));for i=1:N  for j=1:length(x)    % Kernel Gaussiana    K = (1/sqrt(2*pi))*exp(-(1/2)*((X(i)-x(j))/h)^2);    pdf_x(j) = pdf_x(j) + (1/(N*h)) * K;                    endendplot(x,pdf_x)title ("Simulacion Kernel Gaussiana")